import React from "react";
import { useForm } from "react-hook-form";
import { init } from "emailjs-com";
init("user_ufZkh7dVPCrG2nUIrsPby");

export default function Contact(props) {
  const { register, handleSubmit } = props.useForm();

  const onSubmit = (data, r) => {
    alert(`Votre message de la part de ${data.email} a bien été envoyé`);
    const templateId = process.env.REACT_APP_TEMPLATE_ID;
    const serviceID = process.env.REACT_APP_SERVICE_ID;
    sendFeedback(serviceID, templateId, {
      from_name: data.name,
      message_html: data.comment,
      reply_to: data.email,
    });
    r.target.reset();
  };

  const sendFeedback = (serviceID, templateId, variables) => {
    window.emailjs
      .send(serviceID, templateId, variables)
      .then((res) => {
        console.log("Email successfully sent!");
      })
      .catch((err) =>
        console.error(
          "There has been an error.  Here some thoughts on the error that occured:",
          err
        )
      );
  };

  return (
    <div id="contact">
      <h1 className="title">Contactez-moi</h1>
      <form className="contact-form" onSubmit={handleSubmit(onSubmit)}>
        <input
          className="form-input"
          placeholder="Votre Nom"
          name="name"
          ref={register({
            required: `Veuillez inscrire votre nom`,
          })}
        />
        <br />
        {`Veuillez inscrire votre nom`}
        <br />

        <input
          className="form-input"
          placeholder="Votre courriel"
          name="email"
          ref={register({
            required: `Veuillez inscrire votre courriel`,
            pattern: {
              value: /^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,}$/i,
              message: `Mauvais format de courriel`,
            },
          })}
        />
        <br />
        {`Veuillez inscrire votre courriel`}
        <br />

        <textarea
          className="form-input msg"
          placeholder="Votre message"
          name="comment"
          ref={register({
            required: true,
          })}
        />
        <br />
        {`Oupss vous avez oublié votre message`}
        <br />
        <div className="box-1">
          <button className="btn btn-one" type="submit" variant="contained">
            <span>Envoyer !</span>
          </button>
        </div>
      </form>
    </div>
  );
}
